var button=document.querySelector(".search-button"),form=document.querySelector(".search-form"),arrival=form.querySelector("[name=arrival-date]"),departure=form.querySelector("[name=departure-date]");button.addEventListener("click",function(r){r.preventDefault(),form.classList.toggle("switch"),form.classList.remove("error"),arrival.focus()}),form.addEventListener("submit",function(r){arrival.value&&departure.value||(r.preventDefault(),form.classList.remove("error"),form.offsetWidth=form.offsetWidth,form.classList.add("error"))});